INSERT INTO service_faqs (service_page_id, question, answer, faq_order) VALUES
  (863, 'Why does a Pacific Palisades commercial property need a backup generator system?', 'Pacific Palisades businesses—from boutique retail stores to medical offices and hospitality venues—depend on continuous power for operations, inventory protection, and customer service. A professional backup generator ensures your business stays operational during outages, protecting revenue, data, and your reputation. Shaffer Construction designs systems that automatically kick in during power loss, so your operations never skip a beat.', 0),
