INSERT INTO service_faqs (service_page_id, question, answer, faq_order) VALUES
  (873, 'Why do commercial buildings in Pacific Palisades need proper exhaust fan ventilation wiring?', 'Commercial properties in Pacific Palisades-including restaurants, offices, retail spaces, and facilities-require properly designed and installed exhaust ventilation to maintain indoor air quality, remove moisture and odors, and comply with local building codes. Inadequate ventilation can lead to air quality issues, equipment damage, mold growth, and code violations that prevent business operations. Shaffer Construction ensures your exhaust system is wired for reliable, code-compliant operation with proper electrical integration and controls that protect your business.', 1),
